INSERT INTO "courses" VALUES(1,'复旦881','一共考五门专业课，这个课程是五门专业课全讲。',replace(replace('<p><span style="font-size:10.5pt"><span style="font-family:等线">一共考五门专业课，这个课程是五门专业课全讲。</span></span></p>\r\n\r\n<p><span style="font-size:10.5pt"><span style="font-family:等线">课程安排一共分为三轮，第一轮是基础复习，先讲模电模集数电数集后ASIC，并讲解习题；第二轮是提高复习，还是讲解五门专业课，但是会讲得系统点以及讲解一些难点；最后是真题班以及真题预测班。</span></span></p>\r\n\r\n<p><span style="font-size:10.5pt"><span style="font-family:等线">这三轮中间还会有一些学长学姐直播，介绍复习各科复习经验。</span></span></p>\r\n\r\n<p><span style="font-size:10.5pt"><span style="font-family:等线">后面初试完出分之后，再帮助大家进行复试，并帮忙介绍和联系导师，做最大努力帮助大家。</span></span></p>\r\n\r\n<p>附飞姐美照</p>\r\n\r\n<p><img alt="" src="/upload/%E5%9B%BE%E7%89%87%201.png" style="height:50%; width:50%" /></p>\r\n','\r',char(13)),'\n',char(10)),replace(replace('<p>暂时为空</p>\r\n','\r',char(13)),'\n',char(10)),0,'视频','/courseimg/201801051704012354.jpg','2018-01-05 17:04:01.787496',2);
